(99, 'SSD Samsung 860 Evo 250GB M.2 2280 SATA III', '2490000', '/hinhsanpham/860_Evo_M_2_250GB_04.jpg', '/hinhsanpham/860_Evo_M_2_250GB_01.jpg,/hinhsanpham/860_Evo_M_2_250GB_02.jpg,/hinhsanpham/860_Evo_M_2_250GB_03.jpg,/hinhsanpham/860_Evo_M_2_250GB_04.jpg,/hinhsanpham/860_Evo_M_2_250GB_06.jpg', '<p><strong>Ổ cứng&nbsp;SSD Samsung 860 l&agrave; d&ograve;ng ổ cứng được n&acirc;ng cấp từ d&ograve;ng 850 EVO danh tiếng v&agrave; b&aacute;n chạy nhất to&agrave;n cầu,&nbsp;l&agrave; sự lựa chọn tốt nhất d&agrave;nh cho game thủ cả về gi&aacute; v&agrave; hiệu năng hoạt động được khẳng định v&agrave; đ&aacute;nh gi&aacute; chi tiết bởi c&aacute;c tạp ch&iacute; c&ocirc;ng nghệ h&agrave;ng đầu thế giới như Cnet, thessdreview, the PC World. TweakTown..</strong></p>', 10, 54, 3, 1, 0),
(100, 'SSD Samsung NVMe PM981 M.2 PCIe Gen3 x4 256GB ( 2018 )', '2550000', '/hinhsanpham/PM981_256_01_480x480.jpg', '/hinhsanpham/PM981_01_480x480.jpg,/hinhsanpham/PM981_256_01_480x480.jpg', '<p>Ổ cứng&nbsp;SSD Samsung NVMe PM981 M.2 PCIe Gen3 x4&nbsp;tự h&agrave;o về hiệu suất cực cao của giao thức NVMe v&agrave; độ bền của c&ocirc;ng nghệ độc quyền Samsung V-NAND. L&agrave; giải ph&aacute;p l&yacute; tưởng nhất&nbsp;cho c&aacute;c d&ograve;ng m&aacute;y t&iacute;nh v&agrave; Mainboard c&oacute; hỗ trợ cổng PCIe Gen3 x4, d&ograve;ng sản phẩm PM981 cho ph&eacute;p di chuyển tập tin v&agrave; chạy đa nhiệm nhanh hơn ngay cả khi dung lượng gần đầy.<br />Ổ cứng SSD Samsung NVMe PM981 M.2 PCIe Gen3 x4 sử dụng NAND Flash mới nhất v&agrave; bộ điều khiển Controller&nbsp;ti&ecirc;n tiến để mang lại hiệu suất cao, độ tin cậy tuyệt vời v&agrave; tiết kiệm điện, đồng thời bảo vệ c&aacute;c dữ liệu của bạn với chức năng bảo vệ dữ liệu h&agrave;ng đầu. Ổ cứng SSD Samsung NVMe PM981 M.2 PCIe Gen3 x4 c&oacute; tốc độ l&ecirc;n đến 3500 MB/s, đ&aacute;p ứng mọi nhu cầu cần sử dụng hiệu năng cao như đa nhiệm, thao t&aacute;c dữ liệu phức tạp v&agrave; chơi Games. SSDs đại diện cho sự tiến bộ lớn đầu ti&ecirc;n trong c&ocirc;ng nghệ lưu trữ ch&iacute;nh trong nhiều năm.</p>', 10, 54, 3, 1, 0),
(101, 'SSD Samsung 860 Evo 250GB mSATA SATA III', '2730000', '/hinhsanpham/860_Evo_mSATA_250GB_04_480x480.jpg', '/hinhsanpham/860_Evo_mSATA_250GB_01_480x480.jpg,/hinhsanpham/860_Evo_mSATA_250GB_03_480x480.jpg,/hinhsanpham/860_Evo_mSATA_250GB_04_480x480.jpg', '<p><strong>Ổ cứng&nbsp;SSD Samsung 860 l&agrave; d&ograve;ng ổ cứng được n&acirc;ng cấp từ d&ograve;ng 850 EVO danh tiếng v&agrave; b&aacute;n chạy nhất to&agrave;n cầu,&nbsp;l&agrave; sự lựa chọn tốt nhất d&agrave;nh cho game thủ cả về gi&aacute; v&agrave; hiệu năng hoạt động được khẳng định v&agrave; đ&aacute;nh gi&aacute; chi tiết bởi c&aacute;c tạp ch&iacute; c&ocirc;ng nghệ h&agrave;ng đầu thế giới như Cnet, thessdreview, the PC World. TweakTown..</strong></p>', 10, 54, 3, 1, 0),
(102, 'SSD Samsung 970 Pro PCIe NVMe V-NAND M.2 2280 1TB', '17790000', '/hinhsanpham/970_Pro_1TB_01_480x480.jpg', '/hinhsanpham/970_Pro_1TB_01_480x480.jpg,/hinhsanpham/970_Pro_1TB_02_480x480.jpg,/hinhsanpham/970_Pro_1TB_04_480x480.jpg,/hinhsanpham/970_Pro_1TB_05_480x480.jpg', '<p>Ổ cứng SSD Samsung 970 PRO PCIe NVMe M.2 2280 V-NAND&nbsp;được trang bị&nbsp;chuẩn giao tiếp PCIe Gen3 x4 băng th&ocirc;ng rộng mang đến cho bạn một hiệu năng cực k&igrave; tuyệt vời. Với c&ocirc;ng nghệ Samsung V-NAND v&agrave; Dynamic Thermal Guard, ổ cứng SSD Samsung 970 PRO PCIe NVMe M.2 2280 V-NAND&nbsp;đảm bảo t&iacute;nh an to&agrave;n&nbsp;của dữ liệu. Giao diện NVMe v&agrave; Controller&nbsp;Phoenix l&agrave;m gia tăng tốc độ đọc/ghi l&ecirc;n mức tối đa. C&ocirc;ng nghệ TurboWrite gi&uacute;p gia tăng hiệu năng, đ&aacute;p ứng mọi nhu cầu của bạn như chỉnh sửa video, ph&acirc;n t&iacute;ch dữ liệu v&agrave; chơi Games. Ổ cứng SSD Samsung 960 PRO PCIe NVMe M.2 2280 V-NAND&nbsp;đi k&egrave;m&nbsp;phần mềm Magician của Samsung, gi&uacute;p dễ d&agrave;ng quản l&yacute;, theo d&otilde;i v&agrave; tối ưu h&oacute;a hiệu năng của ổ đĩa.</p>', 10, 54, 3, 1, 0),
(103, 'SSD Samsung 970 EVO PCIe NVMe V-NAND M.2 2280 250GB', '3250000', '/hinhsanpham/970_Evo_250_03_480x480.jpg', '/hinhsanpham/970_Evo_0000_480x480.jpg,/hinhsanpham/970_Evo_250_01_480x480.jpg,/hinhsanpham/970_Evo_250_02_480x480.jpg,/hinhsanpham/970_Evo_250_03_480x480.jpg', '<p>Ổ cứng SSD Samsung 970 EVO PCIe NVMe M.2 2280 V-NAND&nbsp;được trang bị&nbsp;chuẩn giao tiếp PCIe Gen3 x4 băng th&ocirc;ng rộng mang đến cho bạn một hiệu năng cực k&igrave; tuyệt vời. Với c&ocirc;ng nghệ Samsung V-NAND v&agrave; Dynamic Thermal Guard, ổ cứng SSD Samsung 970 EVO PCIe NVMe M.2 2280 V-NAND&nbsp;đảm bảo t&iacute;nh an to&agrave;n&nbsp;của dữ liệu. Giao diện NVMe v&agrave; s Controller&nbsp;Polaris l&agrave;m gia tăng tốc độ đọc/ghi l&ecirc;n mức tối đa. C&ocirc;ng nghệ TurboWrite gi&uacute;p gia tăng hiệu năng, đ&aacute;p ứng mọi nhu cầu của bạn như chỉnh sửa video, ph&acirc;n t&iacute;ch dữ liệu v&agrave; chơi Games. Ổ cứng SSD Samsung 960 EVO PCIe NVMe M.2 2280 V-NAND&nbsp;đi k&egrave;m&nbsp;phần mềm Magician của Samsung, gi&uacute;p dễ d&agrave;ng quản l&yacute;, theo d&otilde;i v&agrave; tối ưu h&oacute;a hiệu năng của ổ đĩa.</p>', 10, 54, 3, 1, 0),
(104, 'SSD Samsung 860 Pro Series 256GB', '3750000', '/hinhsanpham/860_Pro_2_5_inch_01.jpg', '/hinhsanpham/860_Pro_2_5_inch_01.jpg,/hinhsanpham/860_Pro_2_5_inch_04.jpg,/hinhsanpham/860_Pro_2_5_inch_07.jpg', '<p>Ổ cứng SSDSamsung 860 PRO Series được trang bị c&ocirc;ng nghệ Samsung 64 Layer 3D V-NAND mới nhất v&agrave; bộ điều khiển Controller dựa tr&ecirc;n thuật to&aacute;n mạnh mẽ MJX của Samsung, dễ d&agrave;ng xử l&yacute; khối lượng c&ocirc;ng việc nặng nề của m&aacute;y t&iacute;nh, m&aacute;y trạm v&agrave; NAS (Network Attached Systems), đ&aacute;p ứng mọi nhu cầu cho game thủ v&agrave; c&aacute;c chuy&ecirc;n gia CNTT. Với hiệu suất đọc/ghi cực cao, độ tin cậy v&agrave; si&ecirc;u tiết kiệm điện năng. Ổ cứng Samsung 860 Pro mang đến tốc độ gấp đ&ocirc;i so với c&aacute;c loại ổ cứng SSD th&ocirc;ng thường với độ bền cực cao v&agrave; được bảo h&agrave;nh 5 năm tại&nbsp;<strong>MemoryZone</strong>.</p>', 10, 54, 3, 1, 0),
(105, 'USB 3.0 SanDisk Ultra Flair CZ73 16GB', '160000', '/hinhsanpham/Sandisk_Ultra_Flair_CZ73_09.jpg', '/hinhsanpham/Sandisk_Ultra_Flair_CZ73_09.jpg,/hinhsanpham/Sandisk_Ultra_Flair_CZ73_10.jpg,/hinhsanpham/Sandisk_Ultra_Flair_CZ73_11.jpg,/hinhsanpham/Sandisk_Ultra_Flair_CZ73_16.jpg', '<p>Với tốc độ chuyển dữ liệu nhanh gấp 15 lần so với chuẩn USB 2.0, USB SanDisk Ultra Flair USB 3.0 Flash Drive được thiết kế để di chuyển c&aacute;c tập tin của bạn thật nhanh ch&oacute;ng, v&igrave; vậy n&oacute; sẽ tiết kiệm cho bạn rất nhiều thời gian. Vỏ bọc được l&agrave;m bằng kim loại bền v&agrave; rất phong c&aacute;ch gi&uacute;p giữ c&aacute;c tập tin quan trọng của bạn an to&agrave;n. B&ecirc;n cạnh đ&oacute; phần mềm SanDisk SecureAccess đi k&egrave;m cho ph&eacute;p bạn dễ d&agrave;ng th&ecirc;m c&aacute;c mật khẩu được m&atilde; h&oacute;a cho c&aacute;c tập tin quan trọng.</p>', 10, 57, 12, 1, 0),
(106, 'USB 3.0 SanDisk Ultra SDCZ48 16GB 100MB/s', '165000', '/hinhsanpham/Sandisk_CZ48_01.jpg', '/hinhsanpham/Sandisk_CZ48_01.jpg,/hinhsanpham/Sandisk_CZ48_02.jpg,/hinhsanpham/Sandisk_CZ48_04.jpg,/hinhsanpham/Sandisk_CZ48_16_01.jpg', '<p><a href=\"https://memoryzone.com.vn/usb-3-0-sandisk-ultra-sdcz48-16gb-100mbs/\">USB SanDisk Ultra USB 3.0 Flash Drive</a>&nbsp;đươc trang bị tốc độ chuyển dữ liệu nhanh hơn v&agrave; khả năng lưu trữ rất cao trong thiết kế rất nhỏ gọn. Tiết kiệm thời gian với tốc độ di chuyển c&aacute;c dữ liệu nhanh hơn gấp 10 lần so với chuẩn USB 2.0. Khả năng lưu trữ l&ecirc;n đến 256GB,&nbsp;USB SanDisk Ultra USB 3.0 Flash Drive đ&aacute;p ứng mọi nhu cầu lưu trữ của bạn.</p>', 10, 57, 12, 1, 0),
(107, 'USB 3.0 SanDisk Ultra Fit CZ43 16GB', '170000', '/hinhsanpham/Sandisk_CZ43_16_03.jpg', '/hinhsanpham/Sandisk_CZ43_16_01.jpg,/hinhsanpham/Sandisk_CZ43_16_02.jpg,/hinhsanpham/Sandisk_CZ43_16_03.jpg,/hinhsanpham/Sandisk_CZ43_16_04.jpg,/hinhsanpham/Sandisk_CZ43_16_05.jpg', '<p>Với tốc độ đọc l&ecirc;n đến 130 MB/s v&agrave; tốc độ ghi nhanh hơn gấp 10 lần so với chuẩn USB 2.0, USB SanDisk Ultra Fit USB 3.0 CZ43 Flash Drive cho ph&eacute;p bạn di chuyển, lưu trữ v&agrave; chia sẻ th&ocirc;ng tin y&ecirc;u th&iacute;ch của bạn một c&aacute;ch nhanh ch&oacute;ng. Với dung lượng l&ecirc;n đến 128GB, mở rộng tối đa kh&ocirc;ng gian lưu trữ cho thiết bị của bạn một c&aacute;ch nhanh ch&oacute;ng hoặc di chuyển c&aacute;c tập tin giữa c&aacute;c m&aacute;y t&iacute;nh x&aacute;ch tay, m&aacute;y t&iacute;nh bảng, m&aacute;y chơi game, TV, hệ thống &acirc;m thanh xe hơi, hoặc thiết bị USB kh&aacute;c. Bạn thậm ch&iacute; c&oacute; thể chuyển một bộ phim độ d&agrave;i đầy đủ chỉ trong 40 gi&acirc;y. Ngo&agrave;i ra, USB SanDisk Ultra Fit USB 3.0 CZ43 Flash Drive tương th&iacute;ch ngượcvới cổng USB 2.0.</p>', 10, 57, 12, 1, 0),
(108, 'USB 2.0 Silicon Power Ultima U03 16GB', '110000', '/hinhsanpham/Ultima_U03_01.jpg', '/hinhsanpham/Ultima_U03_01.jpg,/hinhsanpham/Ultima_U03_02.jpg,/hinhsanpham/Ultima_U03_03.jpg', '<p>Cổ điển với n&eacute;t cắt kim cương<br />Thiết kế tinh tế của&nbsp;<a href=\"http://memoryzone.com.vn/usb-silicon-power-ultima-u03-2-0-16gb/\">USB Silicon Power Ultima U03 2.0</a>&nbsp;rất đơn giản v&agrave; cổ điển. Bề mặt c&oacute; c&aacute;c h&igrave;nh cắt kim cương sang trọng kết hợp với phần nhẵn b&oacute;ng l&agrave;m nổi bật sự sang trọng của bạn. N&oacute; kh&ocirc;ng chỉ l&agrave; một ổ đĩa USB m&agrave; c&ograve;n l&agrave; một phụ kiện thời trang của bạn.</p>', 10, 57, 13, 1, 0);
